mapper.select:
SELECT EXISTS (
       SELECT 1
         FROM participant
        WHERE target_type = 'PROJECT'
          AND target_id   = #{projectId}
          AND user_id     = #{userId}
          AND role_id     = 1
)

SELECT EXISTS (
SELECT 1
FROM participant a
JOIN
WHERE a.target_type = 'PROJECT'
AND a.target_id   = #{projectId}
AND a.user_id     = #{userId}
AND a.role_id     = 2
AND b.dept_name = #{deptName}
AND b.job_rank_name = #{jobRank}
)
