mapper.select:
SELECT EXISTS (
       SELECT 1
         FROM participant
        WHERE target_type = 'PROJECT'
          AND target_id   = #{projectId}
          AND user_id     = #{userId}
          AND role_id     = 1
)

SELECT EXISTS (
SELECT 1
FROM participant
WHERE target_id = #{projectId}
AND user_id = #{userId}
AND target_type = 'PROJECT'
)

SELECT EXISTS (
SELECT 1
FROM participant a
JOIN user b ON a.user_id = b.id
WHERE a.target_type = 'PROJECT'
AND a.target_id   = #{projectId}
AND a.role_id     = 2
AND b.dept_name = #{deptName}
)

SELECT EXISTS (
SELECT 1
FROM participant a
JOIN user b ON a.user_id = b.id
WHERE a.target_type = 'PROJECT'
AND a.target_id = #{projectId}
AND a.user_id = #{userId}
AND (a.role_id = 1 OR a.role_id = 2)
)

SELECT EXISTS (
  SELECT 1
    FROM participant a
    JOIN user b ON a.user_id = b.id
   WHERE a.user_id = #{userId}
    AND a.target_type = 'PROJECT'
    AND a.target_id = #{projectId}
    AND b.dept_name = #{deptName}
)
