mapper.select:
SELECT EXISTS (
       SELECT 1
         FROM participant
        WHERE target_type = 'PROJECT'
          AND target_id   = #{projectId}
          AND user_id     = #{userId}
          AND role_id     = 1
)

SELECT EXISTS (
SELECT 1
FROM participant
WHERE target_id = #{projectId}
AND user_id = #{userId}
AND target_type = 'PROJECT'
)
